SELECT age_band AS ageBand, gender AS gender, COUNT(*) AS cnt FROM ( SELECT CASE WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 20 THEN '10대 이하' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 30 THEN '20대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 40 THEN '30대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 50 THEN '40대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 60 THEN '50대' ELSE '60대+' END AS age_band, CASE WHEN m.RRN_GENDER IN ('1','3') THEN 'M' WHEN m.RRN_GENDER IN ('2','4') THEN 'F' ELSE 'U' END AS gender FROM CARD_APPLICATION_TEMP t JOIN MEMBER m ON m.MEMBER_NO = t.MEMBER_NO WHERE t.CREATED_AT >=
AND t.CREATED_AT <
) GROUP BY age_band, gender ORDER BY age_band, gender

SELECT age_band AS ageBand, gender AS gender, COUNT(*) AS cnt FROM ( SELECT CASE WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 20 THEN '10대 이하' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 30 THEN '20대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 40 THEN '30대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 50 THEN '40대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT, 'YYYYMMDD'))/12) < 60 THEN '50대' ELSE '60대+' END AS age_band, CASE WHEN m.RRN_GENDER IN ('1','3') THEN 'M' WHEN m.RRN_GENDER IN ('2','4') THEN 'F' ELSE 'U' END AS gender FROM CARD_APPLICATION a JOIN MEMBER m ON m.MEMBER_NO = a.MEMBER_NO WHERE TRIM(UPPER(a.STATUS)) IN ('SIGNED','SINGED') AND a.UPDATED_AT >=
AND a.UPDATED_AT <
) GROUP BY age_band, gender ORDER BY age_band, gender

WITH TMP AS ( SELECT t.CARD_NO, CASE WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 20 THEN '10대 이하' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 30 THEN '20대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 40 THEN '30대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 50 THEN '40대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 60 THEN '50대' ELSE '60대+' END AS age_band, CASE WHEN m.RRN_GENDER IN ('1','3') THEN 'M' WHEN m.RRN_GENDER IN ('2','4') THEN 'F' ELSE 'U' END AS gender, 1 AS startsTemp, 0 AS confirmed FROM CARD_APPLICATION_TEMP t JOIN MEMBER m ON m.MEMBER_NO = t.MEMBER_NO WHERE t.CREATED_AT >=
AND t.CREATED_AT <
), FIN AS ( SELECT a.CARD_NO, CASE WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 20 THEN '10대 이하' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 30 THEN '20대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 40 THEN '30대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 50 THEN '40대' WHEN TRUNC(MONTHS_BETWEEN(SYSDATE, TO_DATE( (CASE WHEN m.RRN_GENDER IN ('1','2') THEN '19' ELSE '20' END)||m.RRN_FRONT,'YYYYMMDD'))/12) < 60 THEN '50대' ELSE '60대+' END AS age_band, CASE WHEN m.RRN_GENDER IN ('1','3') THEN 'M' WHEN m.RRN_GENDER IN ('2','4') THEN 'F' ELSE 'U' END AS gender, 0 AS startsTemp, 1 AS confirmed FROM CARD_APPLICATION a JOIN MEMBER m ON m.MEMBER_NO = a.MEMBER_NO WHERE a.STATUS = 'SIGNED' AND a.UPDATED_AT >=
AND a.UPDATED_AT <
) SELECT x.CARD_NO AS cardNo, c.CARD_NAME AS cardName, MIN(DBMS_LOB.SUBSTR(c.CARD_URL, 4000, 1)) AS cardImg, -- ★ CLOB → VARCHAR2 + 집계 x.age_band AS ageBand, x.gender AS gender, SUM(x.startsTemp) AS startsTemp, SUM(x.confirmed) AS confirmed FROM (SELECT * FROM TMP UNION ALL SELECT * FROM FIN) x LEFT JOIN CARD c ON c.CARD_NO = x.CARD_NO GROUP BY x.CARD_NO, c.CARD_NAME, x.age_band, x.gender -- ★ c.CARD_URL 제거 ORDER BY x.age_band, x.gender, SUM(x.confirmed) DESC, SUM(x.startsTemp) DESC
